import { StyleSheet,View, Text, Alert } from 'react-native'
import React, { useState } from 'react'
import Title from '../UI/Title'
import NumberContainer from '../components/NumberContainer'
import PrimaryButton from '../UI/PrimaryButton'
function generateRandomBetween(min, max, exclude) {
  const rndNum = Math.floor(Math.random() * (max - min)) + min;

  if (rndNum === exclude) {
    return generateRandomBetween(min, max, exclude);
  } else {
    return rndNum;
  }
}

const GameScreen = ({value_entered}) => {
  const initialGuess=generateRandomBetween(1,100,value_entered);
  const [currGuess,setCurrGuess]=useState(initialGuess);
  let currValue=initialGuess;
  let minBoundary=1
  let maxBoundary=100

function nextGuessHandler (direction)
{ 
  if(direction=='lower' && currValue>maxBoundary || direction=='higher' && currValue<minBoundary)
  {
    Alert.alert(
      "Alert Title",
    "You have cheated in the game",
    [
      
      { text: "Sorry!",style:'cancel'}
    ]
      );
      return;
  }

  if(direction=='lower')
  {
    currValue=maxBoundary
  }
  else
  {
    currValue=minBoundary;
  }
}
  return (
    <View style={styles.screen}>
     <Title titleText="Opponent's Guess"></Title>
     <NumberContainer Guessed_Number={currGuess}></NumberContainer>
     <View>
      <Text>Higher or Lower</Text>
      <PrimaryButton children="-" onPress={nextGuessHandler.bind("lower")}>+</PrimaryButton>
      <PrimaryButton children="+" onPress={nextGuessHandler.bind("higher")}>-</PrimaryButton>

     </View>
    </View>
  )
}
const styles=StyleSheet.create({
  screen:
  {
    flex:1,
    padding:10,
    marginTop:60
  },
 
})

export default GameScreen
